using System; using Xamarin.Forms; using HowlOut; using HowlOut.Droid; using Xamarin.Forms.Platform.Android; using System.ComponentModel; using Android.Graphics.Drawables; using Android.Views;  [assembly: ExportRenderer(typeof(CustomEditor), typeof(CustomEditorRenderer))] namespace HowlOut.Droid { 	public class CustomEditorRenderer : EditorRenderer 	{ 		protected override void OnElementChanged( 			ElementChangedEventArgs<Editor> e) 		{ 			base.OnElementChanged(e);  			if (e.NewElement != null) 			{ 				var element = e.NewElement as CustomEditor; 				this.Control.Hint = element.Placeholder; 				GradientDrawable gd = new GradientDrawable(); 				gd.SetStroke(5, Color.White.ToAndroid()); 				this.Control.SetBackgroundDrawable(gd); 				this.Control.Gravity = Android.Views.GravityFlags.Center; 				this.Control.SetHintTextColor(Color.White.ToAndroid()); 			} 		}  		protected override void OnElementPropertyChanged( 			object sender, 			PropertyChangedEventArgs e) 		{ 			base.OnElementPropertyChanged(sender, e);  			if (e.PropertyName == CustomEditor.PlaceholderProperty.PropertyName) 			{ 				var element = this.Element as CustomEditor; 				this.Control.Hint = element.Placeholder; 				GradientDrawable gd = new GradientDrawable(); 				gd.SetStroke(5, Color.White.ToAndroid()); 				this.Control.SetBackgroundDrawable(gd); 				this.Control.Gravity = Android.Views.GravityFlags.Center; 				this.Control.SetHintTextColor(Color.White.ToAndroid()); 			} 		} 	} }